runScript("""
  var zoom = "{{zoom|default:100}}"
  var scaleFactor = zoom / 100;

  document.querySelector('body').style.transform = `scale(${scaleFactor})`;
  document.querySelector('body').style.transformOrigin = "center";

  clearTimeout(window.snapToTopTimeout);
  clearTimeout(window.continuousScrollTimeout);

  var auto_scrolling = "{{auto_scrolling|default:False}}"

  if(auto_scrolling == "True") {
    j(document).ready(function() {
        j(window).scrollTop(0);
        const notionScroller = j('div.notion-scroller.vertical');
        function scrollToBottom() {
            if (notionScroller.length === 0) {
              console.error('Element "div.notion-scroller.vertical" not found!');
              return Promise.reject('Element not found');
            }
            return new Promise((resolve) => {
                notionScroller.animate({ scrollTop: notionScroller[0].scrollHeight - notionScroller.height() / scaleFactor }, {{timeToScroll|default:30}} * 1000, function() {
                    resolve();
                });
            });
        }
        function snapToTop() {
            return new Promise((resolve) => {
                window.snapToTopTimeout = setTimeout(function() {
                    notionScroller.scrollTop(0);
                    resolve();
                }, 1000);
            });
        }
        async function continuousScroll() {
            while (true) {
                await scrollToBottom();
                await snapToTop();
            }
        }
        window.continuousScrollTimeout = setTimeout(function() {
            continuousScroll();
        }, 2000);
    });
}
""")

