if("""return '{{allow_scroll_page}}' == 'True' || '{{allow_scroll_page}}' == 'true'"""){
  runScript("""
    if (!!window.scrolling_interval) clearInterval(window.scrolling_interval);
    if(j('div[role="main"]:first-child').length > 0) {
      var el = document.querySelector('div[role="main"]:first-child')
      if(!!document.querySelector("#list-content-id")) el = document.querySelector("#list-content-id")
      else if(j('div.Files-main').length > 0) el = document.querySelector('div.ms-DetailsList-contentWrapper[role="presentation"]')
      var event = document.createEvent("Event");
      event.initEvent("scroll", true, true);
      el.dispatchEvent(event);
      var rect = el.getBoundingClientRect();
      var visibleHeight = Math.max(0, Math.min(rect.bottom, window.innerHeight) - Math.max(rect.top, 0));
      var scrollTopHeight = el.scrollHeight - visibleHeight;
      var scrollTopDuration = scrollTopHeight * 10;
      var allow_scroll = true;
      var scroll_top = true;

      window.scrolling_interval = setInterval(function () {
        if(allow_scroll && scrollTopHeight  > 0){
          if(scroll_top){
            el.animate(
              [
                { transform: "translateY(0px)" },
                { transform: "translateY(-"+ scrollTopHeight +"px)" },
              ],
              {
                delay: 3000,
                fill: "forwards",
                duration: scrollTopDuration,
                iterations: 1,
              }
            );
            allow_scroll = false;
            scroll_top = false
          }
          else{
            el.animate(
              [
                { transform: "translateY(-"+ scrollTopHeight +"px)" },
                { transform: "translateY(0px)" },
              ],
              {
                delay: 3000,
                fill: "forwards",
                duration: 1000,
                iterations: 1,
              }
            );
            allow_scroll = false;
            scroll_top = true
          }
        }
        Promise.all(el.getAnimations().map((animation) => animation.finished)).then(
          () => allow_scroll = true,
        );
      }, 1000);
    }
  """)
}

