    clearTimeout(window.continuousScrollTimeout);
    j(document).ready(function() {
        j(window).scrollTop(0);

        j('body').css({"zoom": "{{zoom|default:100}}%"});

        function scrollToBottom() {
            return new Promise((resolve) => {
                j('html, body').animate({ scrollTop: j(document).height() - j(window).height() }, {{timeToScroll|default:30}} * 1000, function() {
                    resolve();
                });
            });
        }

        function snapToTop() {
            return new Promise((resolve) => {
                window.snapToTopTimeout = setTimeout(function() {
                    j('html, body').scrollTop(0);
                    resolve();
                }, 1000);
            });
        }

        async function continuousScroll() {
            while (true) {
                await scrollToBottom();
                await snapToTop();
            }
        }

        window.continuousScrollTimeout = setTimeout(function() {
            continuousScroll();
        }, 2000);
    });
  """)
  pause(1800)
  refreshAndWait()
}

